$(function() {
    $("form").keydown(function(n) {
        var n = n || event,
        t = n.which || n.keyCode;
        t == 13 && $("#btnLogin").click()
    });
    $("#btnLogin").click(function() {
        var n = $("form"),
        f = $("#username").val().trim(),
        t = $("#password").val().trim(),
        i,
        r,
        u;
        if (f == "") {
            $.alert(nametip);
            return
        }
        if (t == "") {
            $.alert(pwdtip);
            return
        }
        if ($("#validatecode").length > 0 && (i = $("#validatecode").val(), i == "")) {
            $.alert("璇疯緭鍏ラ獙璇佺爜!");
            return
        }
        r = getEncryptPwd(t, keyurl);
        u = $('<input  type="hidden"/>').attr("name", "password").attr("id", "hdpwd").val(r);
        n.append(u);
        n.submit();
        n.find("#hdpwd").remove()
    });
    $.isMobile() != !0 && detectOS()
});